import React from 'react';
import './Dashboard.css';

const meals = {
  breakfast: [{ name: "Bread", kcal: 130 }, { name: "Egg", kcal: 155 }],
  lunch: [{ name: "Lettuce", kcal: 2 }, { name: "Tomato", kcal: 18 }],
  snacks: [{ name: "Apple", kcal: 52 }, { name: "Hummus", kcal: 230 }],
  dinner: [{ name: "Sweet potato", kcal: 86 }, { name: "Carrot", kcal: 40 }],
};

const Dashboard = () => {
  const [meals, setMeals] = useState({
    breakfast: [],
    lunch: [],
    snacks: [],
    dinner: [],
  });

  const [newMeal, setNewMeal] = useState({ name: '', kcal: '', type: 'breakfast' });

  const handleAddMeal = () => {
    if (!newMeal.name || !newMeal.kcal) return;

    setMeals(prev => ({
      ...prev,
      [newMeal.type]: [...prev[newMeal.type], { name: newMeal.name, kcal: Number(newMeal.kcal) }]
    }));

    setNewMeal({ name: '', kcal: '', type: 'breakfast' }); // Reset form
  };

  const totalKcal = Object.values(meals).flat().reduce((sum, item) => sum + item.kcal, 0);

  return (
  <div className="add-meal-form">
    <input
        type="text"
        placeholder="Food name"
        value={newMeal.name}
        onChange={(e) => setNewMeal({ ...newMeal, name: e.target.value })}
    />
    <input
        type="number"
        placeholder="Calories"
        value={newMeal.kcal}
        onChange={(e) => setNewMeal({ ...newMeal, kcal: e.target.value })}
    />
    <select
        value={newMeal.type}
        onChange={(e) => setNewMeal({ ...newMeal, type: e.target.value })}
    >
        <option value="breakfast">Breakfast</option>
        <option value="lunch">Lunch</option>
        <option value="snacks">Snacks</option>
        <option value="dinner">Dinner</option>
    </select>
    <button onClick={handleAddMeal}>Add Meal</button>
    </div>
  );
};

export default Dashboard;
